INSERT INTO `lc_products_info` VALUES (546,549,'es','Casco cerrado azul AGV K-3 SV E2205 top-five continents pinlock','Casco cerrado AGV K-3 SV E2205 top-five continents','<h4>DETALLES DEL PRODUCTO\r\n</h4><h4></h4><p><ul><li>El K-3 SV E2205 es un casco asequible y adaptable con una sorprendente variedad de características, incluyendo una visera parasol interna, impresionante rendimiento aerodinámico, ventilación eficiente y un amplio campo de visión. La envoltura es de construcción termoplástica, y el revestimiento interior es de tejido Dry-Comfort.&nbsp;<br></li></ul></p><h4>\r\nCARACTERÍSTICAS TÉCNICAS&nbsp;</h4><p style=\"text-align: right;\"><ul><li style=\"text-align: left;\">4 EPS (Espuma de Poliestireno).&nbsp;<br></li><li style=\"text-align: left;\">2 tamaños del helmet  shell.&nbsp;<br></li><li style=\"text-align: left;\">Tejidos en DRI-LEX®.&nbsp;<br></li><li style=\"text-align: left;\">IVS (Sistema integrado  de  ventilación) con canales  ahuecados directamente  del helmet  shell.&nbsp;<br></li><li style=\"text-align: left;\">Hebilla de  ajuste  micro-métrico.&nbsp;<br></li><li style=\"text-align: left;\">Multi-density EPS.&nbsp;<br></li><li style=\"text-align: left;\">PVM (mecanismo visor  personalizado).&nbsp;<br></li><li style=\"text-align: left;\">Helmet  shell en resina pintada HIR-TH (RESINA TERMOPLÁSTICA DE ALTA RESISTENCIA).&nbsp;<br></li><li style=\"text-align: left; \">Parasol.&nbsp;<br></li></ul></p><h4>CARACTERÍSTICAS PRINCIPALES\r\n</h4><p><ul><li>Ventilación central para un flujo de aire óptimo.&nbsp;<br></li><li>Mecanismo de visera patentado XQRS  (extra quick release system) en metal para una extracción sin herramientas muy rápida.&nbsp;<br></li><li>Pinlock incluido en la caja.&nbsp;<br></li><li>La forma interna del casco fue diseñada específicamente para disminuir el daño de riesgo a los huesos del collar en caso de una caída.&nbsp;<br></li><li>La visera está equipada con un sistema patentado de micro-apertura.&nbsp;<br></li><li>Homologación europea E2205,  además cuenta con  una puntuación de 4 estrellas otorgada por Sharp.&nbsp;<br></li></ul></p><h4>PESO\r\n</h4><p><ul><li>1.530 kg&nbsp;<br></li></ul></p><p><br></p>','','cascos,casco AGv,cascos AGV,casco LS2,cascos LS2,casco R7,cascos R7,casco Shoei,cascos Shoei,casco para hombre,cascos para hombre,casco para mujer,cascos para mujer,casco unisex,cascos unisex,casco abierto,cascos abiertos,cascos cerrados,casco cerrado,casc','Modelo: K-3 SV E2205 TOP-FIVE CONTINENTS\r\nColor: AZL/AMA/NJA\r\nTallas: M/L, L, XL');
INSERT INTO `lc_products_info` VALUES (551,554,'es','Casco cerrado blanco LS2 chrono FF351','Casco cerrado blanco LS2 chrono FF351','<h4>CALOTA DE POLICARBONATO\r\n</h4><p>\r\nEl policarbonato es un material preparado para recibir impactos y soportar condiciones meteorológicas extremas.&nbsp;</p><h4>\r\nSISTEMA DE VENTILACION&nbsp;</h4><p>\r\nEntradas y salidas de aire para para proporcionar frescor al interior del casco y evacuar el aire caliente.&nbsp;</p><h4>\r\nTRATAMIENTO ANTIARAÑAZOS&nbsp;</h4><p>\r\nPantalla preparada para recibir rayadas e impactos de elementos durante la conducción.&nbsp;</p><h4>\r\nSISTEMA DE DESMONTAJE RAPIDO&nbsp;</h4><p>\r\nNuevo sistema para desmontar la pantalla de forma rápida, sencilla y sin necesidad de herramientas.&nbsp;</p><h4>\r\nTRATAMIENTO ANTI UV&nbsp;</h4><p>La carcaza exterior recibe un tratamiento para resistir la incidencia de los rayos UV</p>','','cascos,casco AGv,cascos AGV,casco LS2,cascos LS2,casco R7,cascos R7,casco Shoei,cascos Shoei,casco para hombre,cascos para hombre,casco para mujer,cascos para mujer,casco unisex,cascos unisex,casco abierto,cascos abiertos,cascos cerrados,casco cerrado,casc','Código: 7103-2208, 7103-2209, 7103-2210\r\nModelo: CHRONO\r\nColor: BCO/NGO\r\nTallas: L');
INSERT INTO `lc_products_info` VALUES (540,543,'es','Casco cerrado AGV K-3 SV E2205 top-la tortuga pinlock','Casco cerrado AGV K-3 SV E2205 top-la tortuga pinlock','<h4>CALOTA\r\n</h4><p>\r\nCalota exterior de resina HIR-TH (High Resistant Thermoplastic) pintada.&nbsp;</p><p>\r\n2 tamaños de calota exterior para un mejor ajuste a cada talla.&nbsp;</p><p>\r\nCalota interior de EPS en 4 tamaños; dimensiones optimizadas utilizando FEM (Finite Element Analysis).&nbsp;</p><h4>\r\nVENTILACION&nbsp;</h4><p>\r\nSistema de ventilación diseñado y optimizado en el túnel de viento, con 5 entradas de aire frontales y 2 extractores traseros.&nbsp;</p><h4>\r\nVISOR&nbsp;</h4><p>\r\nPantalla clara de policarbonato antirayas y antivaho con 100% protección UV.&nbsp;</p><p>\r\nXQRS (X-tra Quick Release System): mecanismo para desmontar y sustituir la pantalla sin herramientas y en pocos segundos.&nbsp;</p><p>\r\nMecanismo de pantalla con sistema de PVS (Perimitrical Visor Seal) para una mejor sellado y protección contra las corrientes de aire y el ruido.&nbsp;</p><p>\r\nVisor solar integrado (ISV) con tratamiento anti rayas; fácilmente desmontable sin herramientas.&nbsp;</p><h4>\r\nINTERIOR&nbsp;</h4><p>\r\nAcolchados interiores 3D totalmente desmontables y lavables; confeccionados en tejido Dry-Comfort con tratamiento higiénico.&nbsp;</p><p>\r\nCubre nariz extraíble.&nbsp;</p><p>\r\nCubre barbilla extraíble.&nbsp;</p><p>\r\nSistema de cierre micrométrico. Anillo antirrobo en la correa.&nbsp;</p>','','cascos,casco AGv,cascos AGV,casco LS2,cascos LS2,casco R7,cascos R7,casco Shoei,cascos Shoei,casco para hombre,cascos para hombre,casco para mujer,cascos para mujer,casco unisex,cascos unisex,casco abierto,cascos abiertos,cascos cerrados,casco cerrado,casc','Modelo: K-3 SV E2205 TOP-LA TORTUGA\r\nColor: VDE/AMA/NGO\r\nTallas: M/L, L, XL');
