INSERT INTO `lc_products_info` VALUES (612,615,'es','Calceta deportiva pro coolmax ALPINESTARS','','Construido con material técnico Coolmax® para mantener los pies fríos y secos.\r\n<p>\r\nSuela Nostatex® para una excelente absorción de la humedad y transpirabilidad.&nbsp;</p><p>\r\nEl brazalete elástico evita el deslizamiento y reduce la acumulación de material.&nbsp;</p><p>\r\nBandas de soporte para arco y pantorrilla para un mejor ajuste y comodidad.&nbsp;</p><p>\r\nAmortiguación de la densidad en el talón, el dedo del pie, la plantilla y el área de Achilles para la comodidad y la durabilidad.&nbsp;</p><p>\r\nTamaño de la rodilla para una cobertura máxima.&nbsp;</p><p>\r\nAnti-bacteriano, no-oloroso.</p>','','','Modelo: PRO COOLMAX\r\nColor: NEGRO/GRIS/ROJO\r\nTallas: S/M, L/XXL');
INSERT INTO `lc_products_info` VALUES (613,616,'es','Mochila lite negro carbon ALPINESTARS','','Funda duradera con paneles de poliéster 420d de peso ligero. \r\n<p>Compartimiento principal grande con bolsillo acolchado para portátil. \r\n</p><p>Organizador interno y bolsillo de malla. \r\n</p><p>Bolsillo exterior de gafas de sol y bolsillos laterales de malla. \r\n</p><p>Panel trasero acolchado con malla de aire 3D y correas de hombro. \r\n</p><p>Sistema innovador de conexión al pecho. \r\n</p><p>Correa de esternón y Correa de cintura extraíble. \r\n</p><p>Dimensiones: 30x16x45cm / 12x6x18 \" .\r\n</p>','','','Modelo: LITE \r\nColor: NEGRO CARBON ');
INSERT INTO `lc_products_info` VALUES (620,623,'es','Protector de pecho A-1 blanco ALPINESTARS','','Protectores de carcasa frontales y traseras construidos con una mezcla de poli-material de rendimiento para una flexibilidad optimizada y un excelente ahorro de peso. Guardia escudo anterior es certificada con la norma EN 14021. CE \r\n<p>\r\nLos depósitos están perforadas para una máxima transpirabilidad y ventilación de enfriamiento, perfecto para los paseos de pistas polvorientas calientes. &nbsp;</p><p>\r\nEspuma blanda y ligera acolchada en todo el chasis para máxima comodidad y ajuste.&nbsp;</p><h4>\r\nPERSONALIZACIÓN&nbsp;</h4><p>\r\nEl panel acolchado con respaldo extraíble permite un ajuste perfecto del soporte del cuello BNS. &nbsp;</p><p>\r\nTirador BNS de contacto suave para un acoplamiento fácil y seguro del BNS. &nbsp;</p><p>\r\nAlmohadillas desmontables y ajustables para regulación de tamaño con extractor BNS integrado. &nbsp;</p><p>\r\nSistema de cierre elástico de riñón ajustable con hebilla de nuevo diseño, rápida y fácil de usar. &nbsp;</p><p>\r\nEl nuevo cierre de guardia significa que un tamaño se adapta a una amplia gama de tipos de carrocería. &nbsp;</p><p>\r\nInnovador sistema de correa Y integrado para el uso modular del protector de espalda con un montaje rápido y cómodo.</p>','','','Modelo: A-1\r\nColor: BLANCO/NEGRO/ROJO\r\nTallas: M/L, XL/XXL');
